import React, { useState } from 'react'
import styled from 'styled-components'

type Props = {
	toggle: boolean
}

type ToggleProps = {
	toggle?: boolean
}

const AddPost = ({toggle}: Props) => {
	const [type, setType] = useState<string>("")
	const _typeHandler = (_type:string)=>{
		setType(_type);
	}
  return (
	<Container toggle={toggle}>
		{type.length > 0 ? <PostContainer>
			<TextArea rows={5} /> 
			<ButtonWrapper>

				<Button toggle={toggle}>Submit</Button>
			</ButtonWrapper>
		</PostContainer>: <Action>
			 <Button onClick={()=> _typeHandler("post")} toggle={toggle}>Add Your Thought</Button>
			 <Button onClick={()=> _typeHandler("memory")} toggle={toggle}>Add Your Memory</Button>
			</Action>}
	</Container>
  )
}

const Container = styled.div<ToggleProps>`
	position: absolute;
	right: ${props => props.toggle ? "80px": "50px"};
	bottom: ${props => props.toggle ? "80px": "50px"};
	opacity: ${props => props.toggle ? 1: 0};
	width: ${props => props.toggle ? "500px": "0px"};
	min-height: ${props => props.toggle ? "200px": "0px"};
	max-height: 400px;
	background-color: #ffffffa3;
	border-radius: 10px;
	transform-origin: bottom right;
	transition: all 0.4s linear;
	display: flex;
	justify-content: center;
	align-items: center;
	padding: 20px;
`;

const Action = styled.div`
	width: 100%;
	min-height: inherit;
	display: flex;
	justify-content: center;
	align-items: center;
	flex-wrap: wrap;
	gap: 20px;
`

const Button = styled.button<ToggleProps>`
	border: none;
	height: 48px;
	padding: 10px;
	background-color: #4834d4;
	color: #fff;
	font-weight: 600;
	transition-delay: 200s;
	opacity: ${props => props.toggle ? 1: 0};
	font-size: 20px;
	border-radius: 4px;
	transition: all 0.1s linear;
	&:hover{
		box-shadow: 5px 5px 20px #4834d4;
	}
	cursor: pointer;
	display: ${props=> props.toggle ? "block": "none"}
`


const PostContainer = styled.div`
	width: 100%;
	min-height: inherit;
	display: flex;
	flex-direction: column;
	gap: 10px;
`;

const TextArea = styled.textarea`
	width: 100%;
	border: none;
	border-radius: 10px;
	resize: none;
	flex: 1;
	padding: 20px;
	background-color: #636e72;
	color: #212121;
`

const ButtonWrapper = styled.div`
	width: 100%;
`

export default AddPost
