import React, { useMemo, memo } from 'react';
import styled from 'styled-components';
import PostCard from './PostCard';
import { RootState } from '../store';
import { StateType } from '../interfacesAndTypes/TState';
import { useSelector, useDispatch } from 'react-redux';
import { PostParamsType } from '../interfacesAndTypes/RequestParams';
import { useFetchDashboardFeed } from '../hooks/dashboardFeed';
import { deletePost } from '../actions/feedAction';

const Dashboard = () => {
  const response = useFetchDashboardFeed();
  const dispatch = useDispatch();
  const _deletePost: StateType = useSelector((state: RootState)=> state.deletePost)
  const posts = useMemo(()=>{
    if(response.data){
      return response.data.posts;
    }else{
      return []
    }
  },[response])


  return (
	<Container>
    <PostContainer>
      {posts.length > 0  ? posts.map((post: PostParamsType)=>{
        return <PostCard key={post._id} data={post}/>
      }) : <Message>Share some memory to your wall</Message> }
    </PostContainer>
  </Container>
  )
}

const Container = styled.div`
  flex: 1;
  width: 100%;
  display: flex;
  justify-content: center;
`

const PostContainer = styled.div`
  width: 500px;
  display: flex;
  flex-direction: column;
  gap: 20px;
`

const Button = styled.button`
  border: none;
	padding: 10px;
	background-color: #4834d4;
	color: #fff;
	font-weight: 600;
	font-size: 20px;
	border-radius: 4px;
	transition: all 0.1s linear;
	&:hover{
		box-shadow: 5px 5px 20px #4834d4;
	}
	cursor: pointer;
`

const Message = styled.h1`
  color: #636e72;
`
export default memo(Dashboard)
