import React, { useState } from 'react'
import styled from 'styled-components'

const AddPost = () => {
	const [type, setType] = useState<string>("")
  return (
	<Container>
		{type.length > 0 ? <h1>Post</h1>: <Action>
			 <Button>Add Your Thought</Button>
			 <Button>Add Your Memory</Button>
			</Action>}
	</Container>
  )
}

const Container = styled.div`
	position: absolute;
	right: 80px;
	bottom: 80px;
	width: 500px;
	min-height: 200px;
	max-height: 400px;
	background-color: #ffffffa3;
	border-radius: 10px;
	display: flex;
	justify-content: center;
	align-items: center;
	padding: 20px;
`;

const Action = styled.div`
	width: 100%;
	min-height: inherit;
	display: flex;
	justify-content: center;
	gap: 20px;
`

export default AddPost
