import React, { useMemo, memo, Component } from 'react';
import styled from 'styled-components';
import PostCard from './PostCard';
import { connect } from 'react-redux';
import { PostParamsType } from '../interfacesAndTypes/RequestParams';
import { AppDispatch, RootState } from '../store';
import { getFeed } from '../actions/feedAction';

type Props = {
  getPosts?: any,
  posts?: any,
  deletePost?: any,
  deletePostResponse?: any

}

type State = {
  posts: any
  deleteId: string
}


class Dashboard extends Component<Props, State> {

  constructor(props: Props){
    super(props);
    this.state = {
      posts: [],
      deleteId: ""
    }
  }

  componentDidMount(){
    this.props.getPosts();
  }


  componentDidUpdate(prevProps: Props){
    if(this.props.posts && this.props.posts.data && this.props.posts.data != prevProps.posts.data ){
      this.setState({
        posts: [...this.props.posts.data.posts]
      })
    }

    if(this.props.deletePostResponse && this.props.deletePostResponse.data && this.props.deletePostResponse.data != prevProps.deletePostResponse.data){
      const { deleteId } = this.state;
      this.setState({
        posts: [...this.state.posts.filter((post: any)=> post._id != deleteId)],
        deleteId: ""
      })
    }
  }

  _deletePost = (id:string)=>{
    const params = {
      id
    }
    this.setState({
      deleteId: id
    },this.props.deletePost(params))

  }

  render(){

  return (
	<Container>
    <PostContainer>
      {this.state.posts.length > 0  ? this.state.posts.map((post: PostParamsType)=>{
        return <PostCard handleDelete={this._deletePost} key={post._id} data={post}/>
      }) : <Message>Share some memory to your wall</Message> }
    </PostContainer>
  </Container>
  )
    }
}

const Container = styled.div`
  flex: 1;
  width: 100%;
  display: flex;
  justify-content: center;
`

const PostContainer = styled.div`
  width: 500px;
  display: flex;
  flex-direction: column;
  gap: 20px;
`

const Button = styled.button`
  border: none;
	padding: 10px;
	background-color: #4834d4;
	color: #fff;
	font-weight: 600;
	font-size: 20px;
	border-radius: 4px;
	transition: all 0.1s linear;
	&:hover{
		box-shadow: 5px 5px 20px #4834d4;
	}
	cursor: pointer;
`

const Message = styled.h1`
  color: #636e72;
`

const mapStateProps = (state: RootState)=>{
  return {
    posts: state.dashboardFeed
  }
}


const mapDispatchToProps = (dispatch: AppDispatch)=>{
  return  {
    getPosts: ()=> dispatch<any>(getFeed())
  }
}

export default connect(mapStateProps, mapDispatchToProps)(Dashboard)
