import { getActionsStates } from "../Utils/storeHelpers";
import { defaultState, errorState, successState, loadingState } from "../Utils/defaultStates";
import { StateType, ActionType } from "../interfacesAndTypes/TState";
import { GET_FEED, GET_PRESIGNED_URL, CREATE_POST } from "../actions/Types";

function feedResponse(state: StateType, action: ActionType){
	switch(action.type){
		case getActionsStates(GET_FEED).success:
			return {
				...successState,
				data: action.data
			}
		case getActionsStates(GET_FEED).loading:
			return {
				...loadingState,
				loading: action.isLoading
			}
		case getActionsStates(GET_FEED).error:
			return {
				...errorState,
				error: action.error
			}
		default:
			return defaultState
	}
}



function presignedURLResponse(state: StateType, action: ActionType){
	switch(action.type){
		case getActionsStates(GET_PRESIGNED_URL).success:
			return {
				...successState,
				data: action.data
			}
		case getActionsStates(GET_PRESIGNED_URL).loading:
			return {
				...loadingState,
				loading: action.isLoading
			}
		case getActionsStates(GET_PRESIGNED_URL).error:
			return {
				...errorState,
				error: action.error
			}
		default:
			return defaultState
	}
}


function createPostResponse(state: StateType, action: ActionType){
	switch(action.type){
		case getActionsStates(CREATE_POST).success:
			return {
				...successState,
				data: action.data
			}
		case getActionsStates(CREATE_POST).loading:
			return {
				...loadingState,
				loading: action.isLoading
			}
		case getActionsStates(CREATE_POST).error:
			return {
				...errorState,
				error: action.error
			}
		default:
			return defaultState
	}
}



export {
	feedResponse,
	presignedURLResponse,
	createPostResponse
}
