import React from 'react';
import styled from 'styled-components';
import PostCard from './PostCard';
import { useFetchDashboardFeed } from '../hooks/dashboardFeed';

const Dashboard = () => {
  const {data: {posts}} = useFetchDashboardFeed();
  return (
	<Container>
    <PostContainer>
      {posts.length > 0  ? <PostCard /> : <Button>Add a New post</Button> }
    </PostContainer>
  </Container>
  )
}

const Container = styled.div`
  flex: 1;
  width: 100%;
  display: flex;
  justify-content: center;
`

const PostContainer = styled.div`
  width: 500px;
`

const Button = styled.button`
  border: none;
	padding: 10px;
	background-color: #4834d4;
	color: #fff;
	font-weight: 600;
	font-size: 20px;
	border-radius: 4px;
	transition: all 0.1s linear;
	&:hover{
		box-shadow: 5px 5px 20px #4834d4;
	}
	cursor: pointer;
`

export default Dashboard
