import { useState } from 'react'
import styled from 'styled-components'


export const Login = () => {
	const [otpSent, setOtpSent] = useState(false)
  return (
	<Container>
		{otpSent ?<Section>
			<Header>Verify</Header>
			<Input placeholder="212121" />
			<Button>Submit</Button>
		</Section> :<Section>
			<Header>Login</Header>
			<Input placeholder='(21)2121212121' />
			<Button>Get OTP</Button>
		</Section>}
	</Container>
  )
}


const Container = styled.div`
	width: 100%;
	min-height: inherit;
	display: flex;
	justify-content: center;
	align-items: center;

`

const Section = styled.section`
	width: 450px;
	height: 300px;
	background-color: #ffffffa1;
	border-radius: 10px;
	backdrop-filter: blur(10px);
	padding: 20px;
	display: flex;
	flex-direction: column;
	gap: 20px;
	align-items: center;
	justify-content: center;
`;

const Header = styled.h1`
	font-size: 40px;
	color: #636e72;
	width: 100%;
	text-align: center;
`;


const Input = styled.input`
	width: 100%;
	border: none;
	height: 48px;
	border-radius: 4px;
	background-color: #fff;
	padding: 0px 10px;
	box-shadow: 1px 1px 10px #ff868a;
`

const Button = styled.button`
	border: none;
	padding: 10px;
	background-color: #4834d4;
	color: #fff;
	font-weight: 600;
	font-size: 20px;
	border-radius: 4px;
	transition: all 0.1s linear;
	&:hover{
		box-shadow: 5px 5px 20px #4834d4;
	}
	cursor: pointer;
`;

const InputWrapper = styled.div`
	display: "flex"
	flex-direction: column;
`;


