import { useEffect, useState } from 'react'
import styled from 'styled-components'
import { getOTP } from '../actions/usersAction'
import { useSelector, useDispatch } from 'react-redux'
import { RootState } from '../store';
import { StateType } from "../interfacesAndTypes/TState"
import { useAuth } from '../Context/AuthContext';

export const Login = () => {
	const auth = useAuth();
	const [otpSent, setOtpSent] = useState(false)
	const [phoneNumber, setPhoneNumber] = useState("");
	const [otp, setOtp] = useState("");
	const response: StateType  = useSelector((state: RootState)=> state.loggedin);
	const dispatch = useDispatch();

	const _getOTP = ()=>{
		dispatch<any>(getOTP({phoneNumber}))
	}

	const _verfiyOTP = ()=>{
		if(phoneNumber.length>0 && phoneNumber.length===13 && otp.length === 6){
			const params = {
				phoneNumber,
				otp
			}

			auth.signin(params);
		}
	}

	useEffect(()=>{
		if(response.success){
			setOtpSent(true);
		}
	},[response])
  return (
	<Container>
		{otpSent ?<Section>
			<Header>Verify OTP</Header>
			<InputWrapper>
				<SubLabel>Enter your otp</SubLabel>
				<Input name="otp" onChange={(e)=>{
					setOtp(e.target.value)
				}} placeholder="212121" />
			</InputWrapper>
			<Button>Submit</Button>
		</Section> :<Section>
			<Header>Login</Header>
			<InputWrapper>
				<SubLabel>Please enter phone number with country code</SubLabel>
				<Input name="phoneNumber" onChange={(e)=>{
					setPhoneNumber(e.target.value)
				}} placeholder='(21)2121212121' />
			</InputWrapper>
			<Button onClick={_getOTP}>Get OTP</Button>
		</Section>}
	</Container>
  )
}


const Container = styled.div`
	width: 100%;
	min-height: inherit;
	display: flex;
	justify-content: center;
	align-items: center;

`

const Section = styled.section`
	width: 450px;
	height: 300px;
	background-color: #ffffffa1;
	border-radius: 10px;
	backdrop-filter: blur(10px);
	padding: 20px;
	display: flex;
	flex-direction: column;
	gap: 20px;
	align-items: center;
	justify-content: center;
`;

const Header = styled.h1`
	font-size: 40px;
	color: #636e72;
	width: 100%;
	text-align: center;
`;


const Input = styled.input`
	width: 100%;
	border: none;
	height: 48px;
	border-radius: 4px;
	background-color: #fff;
	padding: 0px 10px;
	box-shadow: 1px 1px 10px #ff868a;
`

const Button = styled.button`
	border: none;
	padding: 10px;
	background-color: #4834d4;
	color: #fff;
	font-weight: 600;
	font-size: 20px;
	border-radius: 4px;
	transition: all 0.1s linear;
	&:hover{
		box-shadow: 5px 5px 20px #4834d4;
	}
	cursor: pointer;
`;

const SubLabel = styled.label`
	font-size: 16px;
	color: #636e72;
`

const InputWrapper = styled.div`
	display: flex;
	flex-direction: column;
	gap: 10px;
	font-weight: 500;
`;

