import axios, { AxiosRequestConfig, AxiosResponse } from "axios";
import { ActionCreator, Action, AnyAction, Dispatch } from "redux";
import { AppDispatch } from "../store";
import { ThunkDispatch } from "redux-thunk";

const config: AxiosRequestConfig = {
	baseURL: "http://localhost:5000/api/v1"
}

const instance = axios.create(config);
const headers = {
	'Authorization': `Bearer ${localStorage.getItem('TOGGLE_AUTH_TOKEN')}`,
	'Content-Type': "application/json"
}

const makeRequest = (dispatch: Dispatch, reqObject: AxiosRequestConfig,loadingAction: ActionCreator<Action>, successAction: ActionCreator<Action>, errorAction: ActionCreator<Action>)=>{
	dispatch(loadingAction(true))
	instance(reqObject).then((res: AxiosResponse)=>{

		dispatch(successAction(res.data))

	}).catch(error=>{
		dispatch(errorAction(error))
	})

}

const sendRequest = {
	get: (dispatch: AppDispatch, url: string, params: any, loadingAction: ActionCreator<Action>, successAction: ActionCreator<Action>, errorAction: ActionCreator<Action> )=>{
		let reqObj: AxiosRequestConfig = {
			url,
			method: "GET",
			headers: headers,
			params: params,
		}

		return makeRequest(dispatch, reqObj, loadingAction, successAction, errorAction);
	},
	post: (dispatch: Dispatch, url: string, params: any, loadingAction: ActionCreator<Action>, successAction: ActionCreator<Action>, errorAction: ActionCreator<Action> )=>{
		let reqObj: AxiosRequestConfig = {
			url,
			method: "POST",
			headers: headers,
			params: params,
		}

		return makeRequest(dispatch, reqObj, loadingAction, successAction, errorAction);
	}
}

export {
	sendRequest
}
